-- CRIAÇÃO DA TABELA E O INDEX NA TABELA IMPORTAÇÃO ANITTA
CREATE TABLE public.importacao_anitta (
	imov_id int4 NULL,
	anomesref int4 NULL,
	dbtp_id_crti_id int4 NULL,
	valor numeric(8,2) NULL,
	tipo bpchar(1) NULL
)
WITH (
	OIDS=FALSE
) ;
CREATE INDEX idx_importacao_anitta_anomesref ON public.importacao_anitta (anomesref int4_ops) ;
CREATE INDEX idx_importacao_anitta_imov_id ON public.importacao_anitta (imov_id int4_ops) ;

-- IMPORTAR O ARQUIVO TXT DO ANITTA.

-- CONTA DE AGUA-4 E ESGOTO-5 COM OS VALORES DIFERENTES NO ANITTA QUE NAO FORAM PAGAS
select 
       fc.imov_id,
       fc.cnta_id,
       fc.cnta_amreferenciaconta,
       ia.dbtp_id_crti_id,
       fc.cnta_vlagua,
       fc.cnta_vlesgoto,
       ia.valor   
from faturamento.conta as fc
--JOIN faturamento.debito_cobrado AS b ON fc.cnta_id = b.cnta_id
inner join public.importacao_anitta as ia on ia.imov_id = fc.imov_id and fc.cnta_amreferenciaconta = ia.anomesref
left join arrecadacao.pagamento as ap on fc.cnta_id = ap.cnta_id 
where ap.cnta_id is null
  and fc.dcst_idatual = 0
  and ((ia.dbtp_id_crti_id = 4 and fc.cnta_vlagua <> ia.valor)     
  or (ia.dbtp_id_crti_id = 5 and fc.cnta_vlesgoto <> ia.valor))
  and ia.tipo = 'e'   
group by fc.imov_id,
		 fc.cnta_id,
	     fc.cnta_amreferenciaconta,
	     fc.cnta_vlagua,
	     fc.cnta_vlesgoto,
	     ia.valor,
	     ia.dbtp_id_crti_id
     
