-- CONTAS DEBITO
select * from (SELECT 
       fc.imov_id,
       fc.cnta_id,
       fc.cnta_amreferenciaconta,
       ia.dbtp_id_crti_id,
       fdc.dbtp_id,
       ia.tipo,
       sum(fdc.dbcb_vlprestacao) dbcb_vlprestacao,
       sum(ia.valor) valor
from faturamento.conta as fc
inner join public.importacao_anitta as ia on ia.imov_id = fc.imov_id
left join arrecadacao.pagamento as ap on fc.cnta_id = ap.cnta_id
left join faturamento.debito_cobrado as fdc on fdc.cnta_id = fc.cnta_id and dbtp_id_crti_id= fdc.dbtp_id
where ap.cnta_id is null
  and fc.dcst_idatual = 0
  and fc.cnta_amreferenciaconta = ia.anomesref
  and ia.dbtp_id_crti_id not in (4,5)
  and ia.tipo = 'e' 
group by fc.imov_id,
   fc.cnta_id,
      fc.cnta_amreferenciaconta,      
      ia.dbtp_id_crti_id,         
         fdc.dbtp_id,
         ia.tipo) a where dbcb_vlprestacao <> valor or dbcb_vlprestacao is null 
 
         
