-- CONTAS DE CREDITO     
select * from (SELECT 
       fc.imov_id,
       fc.cnta_id,
       fc.cnta_amreferenciaconta,
       ia.dbtp_id_crti_id,
       ia.tipo,              
       fcr.crti_id,       
       sum(fcr.crrz_vlcredito) crrz_vlcredito,
       sum(ia.valor) valor
from faturamento.conta as fc
inner join public.importacao_anitta as ia on ia.imov_id = fc.imov_id
left join arrecadacao.pagamento as ap on fc.cnta_id = ap.cnta_id
left join faturamento.credito_realizado as fcr on fcr.cnta_id = fc.cnta_id and dbtp_id_crti_id = fcr.crti_id
where ap.cnta_id is null
  and fc.dcst_idatual = 0
  and fc.cnta_amreferenciaconta = ia.anomesref
  and ia.dbtp_id_crti_id not in (4,5)
  and ia.tipo = 's' 
group by fc.imov_id,
         fc.cnta_id,
         fc.cnta_amreferenciaconta,      
         ia.dbtp_id_crti_id,         
         fcr.crti_id,
         ia.tipo) a 
where crrz_vlcredito <> valor or crrz_vlcredito is null 

