--GERA UPDATE DO RA
select
	'update atendimentopublico.registro_atendimento set AMEN_ID = 1, rgat_cdsituacao = 2, rgat_tmencerramento = now(), rgat_dsparecerencerramento = ' || coalesce( rgat_dsparecerencerramento,
