from
	(
	select
		ra.rgat_id,
		ra.AMEN_ID,
		ra.rgat_tmencerramento,
		ra.rgat_dsparecerencerramento,
		ra.rgat_tmultimaalteracao,
		ra.rgat_dsobservacao
	from
		atendimentopublico.registro_atendimento ra
	where
		 ra.rgat_id in (
						select
							 rgat_id
						from atendimentopublico.ordem_servico as os
						where
						     orse_tmgeracao < '2019-06-01 00:00:00'
							 and orse_cdsituacao = 1
							 AND os.amen_id is null 
							 and os.svtp_id in (3226,3234,3235,801,3247)
		   			   )
		   			   order by rgat_id
	) table1;



